package com.example.astrohelper

import android.content.Context
import androidx.room.Room
import kotlinx.coroutines.Dispatchers
import kotlinx.coroutines.GlobalScope
import kotlinx.coroutines.launch

object DatabaseInitializer {
    fun initialize(context: Context) {
        GlobalScope.launch(Dispatchers.IO) {
            val database = Room.databaseBuilder(
                context,
                Database::class.java, "app_database"
            )
                .fallbackToDestructiveMigration()
                .build()

            initializeMagicBall(database)
            initializeTarot(database)
            initializeNumerology(database)
            /*GlobalScope.launch(Dispatchers.IO) {
              // numerologyDao.deleteAll()
             //  magicBallDao.deleteAll() // Удалить все данные из magic_table
             //  tarotDao.deleteAll() // Удалить все данные из tarot_table
           }*/
        }
    }

    private fun initializeMagicBall(database: Database) {
        GlobalScope.launch(Dispatchers.IO) {
            val magicBallDao = database.MagicBallDao()
            val existingCount = magicBallDao.getMagicBallCount()
            if (existingCount == 0) {
                magicBallDao.insertAll(
                    MagicBall(1, "Бесспорно"),
                    MagicBall(2, "Предрешено"),
                    MagicBall(3, "Никаких сомнений"),
                    MagicBall(4, "Определённо да"),
                    MagicBall(5, "Можешь быть уверен в этом"),

                    MagicBall(6, "Мне кажется — «да»"),
                    MagicBall(7, "Вероятнее всего"),
                    MagicBall(8, "Хорошие перспективы"),
                    MagicBall(9, "Знаки говорят — «да»"),
                    MagicBall(10, "Да"),

                    MagicBall(11, "Пока не ясно, попробуй снова"),
                    MagicBall(12, "Спроси позже"),
                    MagicBall(13, "Лучше не рассказывать"),
                    MagicBall(14, "Сейчас нельзя предсказать"),
                    MagicBall(15, "Сконцентрируйся и спроси опять"),

                    MagicBall(16, "Даже не думай"),
                    MagicBall(17, "Мой ответ — «нет»"),
                    MagicBall(18, "По моим данным — «нет»"),
                    MagicBall(19, "Перспективы не очень хорошие"),
                    MagicBall(20, "Весьма сомнительно")
                )
            }
        }
    }
    private fun initializeNumerology(database: Database) {
        GlobalScope.launch(Dispatchers.IO) {
            val numerologyDao = database.numerologyDao()
            val existingCountNumerology = numerologyDao.getNumerologyCount()
            if (existingCountNumerology == 0) {
                numerologyDao.insertNumerology(
                    NumerologyEntity(1, 1,"- Маг. Человек умный, хитрый, человек у которого изначально есть огромный потенциал, который ему нужно реализовать. В минусе - обманщик и искусный манипулятор, слабовольный, использующий свои способности в корыстных целях"),
                    NumerologyEntity(2, 2,"- Верховная жрица. Это хорошая интуиция, умение считывать мысли и желания окружающих и подверженность влиянию со стороны. В минусе - не умеющие хранить тайны, пытающийся получить выгоду из всего, прекрасно играющие на чужих чувствах, как правило, это люди с двойным дном."),
                    NumerologyEntity(3, 3,"- Императрица. Сильная личность, ответственный подход, им также присуще сильное стремление к комфорту и материальному благополучию, хорошие отношения с женщинами. В минусе у женщин - авторитаризм, желание все и всех контролировать, плохие отношения с матерью, у мужчин - измены, желание быть на содержании и также тяжелые отношения с матерью"),
                    NumerologyEntity(4, 4,"- Император. Властный, активный и уверенный в себе человек, аналитический склад ума и любовь к дисциплине. В минусе у мужчин - агрессивный, деспот, упрямый самодур, стремятся к власти любой ценой, у женщин - попытка взять всех под контроль, агрессивность, доминирование в семье."),
                    NumerologyEntity(5, 5,"- Верховный жрец. Люди, умеющие быстро считывать и понимать верхние слои информации, что позволяет им быстро осваивать новые темы, своего рода педанты, чистоплотные. В минусе - лицемерные пустословы, невозможно гордые, считающие себя единственно верными, и не признающие иные точки зрения."),

                    NumerologyEntity(6, 6,"- Влюбленные. Люди с отличным чувством стиля, пониманием красоты, часто неуверенные в себе. К сожалению, такие люди часто сталкиваются с выбором, например, одновременно два любовных партнера, два дома. В минусе - человеку не везет в любви, он постоянно живет в иллюзиях, может иметь несколько партнеров, низкая самооценка и нелюбовь к себе, постоянный поиск того, что стоит визуально изменить в себе."),
                    NumerologyEntity(7, 7,"- Колесница. Человек смелый, хорошо работающий в команде, смел, уверен в себе. Людям с колесницей желательно получить водительские права и путешествовать. В минусе - слишком громкие люди, не хотят в работать в команде, могут целыми днями лениться."),
                    NumerologyEntity(8, 8,"- Справедливость. Люди понимающие, выбирающие нейтральную сторону в конфликтах, усидчивые, пунктуальные и хорошо переносящие рутину (если нет 22, 10 и 7 арканов). В минусе - излишнее упрямство и усердие для того, чтобы доказать свою правоту, критика и сплетни в адрес других людей."),
                    NumerologyEntity(9, 9,"- Отшельник. Человек замкнутый, который моментами предпочитают побыть в одиночестве, так как в эти момент уходит в обучение и анализ. Люди с отшельником полны мудрости, склонны к глубокому анализу и передачи знаний узкому кругу. В минусе - закрытые люди, зацикленность на своих проблемах, отказ от передачи знаний."),
                    NumerologyEntity(10, 10,"- Колесо фортуны. Человека можно охарактеризовать, как самого удачливого из неудачников, это те люди, которые попадут в катастрофу, но выйдут сухими из воды. У таких людей присутствуют периоды, когда хорошо, и когда плохо, поэтому очень важно, когда хорошо - выжать из этого максимум и отложить деньги, когда плохо - дать себе возможность отдохнуть. В минусе - постоянное невезение, скучная жизнь, постоянная пассивность."),

                    NumerologyEntity(11, 11,"- Сила. Смелые, отчаянные ребята, очень сильные и энергичные личности. У них изначально больше энергии, чтобы они смогли реализовать свой потенциал. Рекомендован спорт, для того чтобы нереализованная энергия не уходила в агресиию. В минусе - неконтролируемая агрессия и вспыльчивость, лень, жестокость."),
                    NumerologyEntity(12, 12,"- Повешенный. Часто добрые и мягкие люди, могут уходить в жертвенность, ставя свои интересы ниже интересов других, часто встречается аркан у творческих людей. В минусе - подверженность чужому мнению, легко обмануть, жертвенность."),
                    NumerologyEntity(13, 13,"- Смерть. Хорошие интеллектуальные способности, хорошо развита интуиция, такие люди часто переживают сильные трансформации, им очень важно научиться не бояться их, а извлекать из них урок. В минусе - желание держаться за прошлое, неумение отпускать, страх сделать шаг в будущее, нерешительность, одни и те же болезненные уроки."),
                    NumerologyEntity(14, 14,"- Умеренность. Такие люди часто спокойные, терпимые, не терпят конфликтных ситуаций (если нет 3, 4, 5, 8, 11, 16 или 13 аркана, если же есть - дает большее спокойствие и контроль над необузданностью энергий арканов, перечисленных ранее). Рекомендовано принимать длительную ванну либо водные процедуры. В минусе - взбалмошные, не знают меры, появляются зависимости и злоупотребления."),
                    NumerologyEntity(15, 15,"- Дьявол. Нестабильный аркан, периодически будет в плюсе, периодически в минусе. Такие люди испытывают азарт, горячую потребность жить с интересом, у них есть способности к политике, бизнесу и ораторскому мастерству. В минусе - опасные, используют любые средства для удовлетворения собственных желаний, могут находится в зависимых отношения(либо сами будут зависимы, либо будут зависеть от них)."),

                    NumerologyEntity(16, 16,"- Башня. Ранимые, склонные к переживаниям и волнениям по всякому поводу и без повода люди, также часто проходят болезненные трансформации, где кажется, что все рушится, но важно понимать, что вы построете все снова и это будет лучше, чем было до этого. В минусе - безумно упрямые, их практически невозможно переубедить, открыть глаза на их же проблемы."),
                    NumerologyEntity(17, 17,"- Звезда. Люди, которые часто находятся в центре внимания, легко находят общий язык с окружающими, имеют много друзей. В минусе - ленивые, бездеятельные, не желающие развивать свой творческий потенциал."),
                    NumerologyEntity(18, 18,"- Луна. Люди с хорошо развитой интуицией, имеют творческое и живое воображение, чуткость к окружающим. В минусе - живут в вымышленном мире, не может отличить, кто хороший, а кто плохой, нередко уходят в себя."),
                    NumerologyEntity(19, 19,"- Солнце. Яркие, сильные люди, часто оптимисты, общительны, даже в сложной ситуации, не унывают, а стараются найти наилучший выход. В минусе - эгоцентризм, пытаясь привлечь внимание окружающих, часто бывают истеричными, капризными и неуравновешенными, может пойти на гнусные поступки ради власти."),
                    NumerologyEntity(20, 20,"- Страшный суд. Часто спокойные, мудрые люди, миролюбимые, с отличной интуицией. В минусе - зажатые, неуверенные в себе люди, нерешительные и пассивные, опирающиеся на мнение других."),

                    NumerologyEntity(21, 21,"- Мир. Аркан часто дает хорошую жизнь своему обладателю, такие люди слишком мудрые, многосторонние, любознательные, информированные. В минусе - меланхоличны, могут появляться зависимости, злоупотребления и невоздерженности."),
                    NumerologyEntity(22, 22,"- Шут. Самый сильный аркан, потому что он изначально чистый лист, он не ограничен и может стать кем угодно, часто в самые трудные моменты их выручает сама судьба, их вполне можно назвать удачливами. В минусе - капризный ребенок, безответственный, отсутствие моральных принципов, плывущий по течению, подвержен зависимостям и вредным привычкам.")
                )
            }
        }
    }
    private fun initializeTarot(database: Database) {
        GlobalScope.launch(Dispatchers.IO) {
            val tarotDao = database.tarotDao()
            val existingCountTarot = tarotDao.getMagicBallCountTarot()
            // добавление данных в базу tarot
            if (existingCountTarot==0) {
                tarotDao.insertAll(
                    TarotItem(1, "Карта Таро говорит о том, что человеку необходимо проявить свою инициативу, ведь он значительно хорошо вооружен, и сможет показать свою логику и благоразумие. Даже если в этот день ему обязательно нужно будет срочно принимать решение, то ничего не помешает ему это сделать. По крайней мере, день очень благоприятный.", "mag.jpg"),
                    TarotItem(2, "Карта Таро говорит о том, что человеку нужно прислушаться к внутреннему голосу и действовать согласно ситуации. В этот день стоит положиться на интуицию. Не следует искать объяснений и оправданий тем вопросам, которые не хочется принимать. Карта может скрывать тайны и внутреннюю мудрость, а также напомнить человеку о том, что он забыл о чем-то важном.", "highpriestess.jpg"),
                    TarotItem(3, "Карта Таро говорит о том, что эта карта принесет увлекательный день. У человека появится достаточно много интересных идей, а также творческих замыслов. То, что долго было неосуществимым, в этот день осуществится. И если совсем недавно человек просто махнул рукой на обстоятельства, он заметит, как они начнут бурно развиваться в лучшую сторону.", "empress.jpg"),
                    TarotItem(4, "В качестве карты дня Император является хорошим предзнаменованием. Если у него есть какое-либо сложное дело или проблема, решение которой долго откладывалось, надо взяться именно за это. Действовать нужно решительно, но взвешенно и основательно. Если же конкретной проблемы нет, Император советует просто попробовать воспользоваться ситуаций и переделать побольше поточных дел.", "emperor.jpg"),
                    TarotItem(5, "Если вам выпал Верховный Жрец, то это получение толкового, разумного совета, подсказка о выходе из ситуации. Либо человек сам может стать учителем для кого-либо. Но не стоит самоутверждаться за счёт другого. Вас могут вовлечь в какой-либо конфликт, но тогда вам лучше всего постараться либо избежать его, либо решить вопрос, не пренебрегая моралью.", "hierophant.jpg"),

                    TarotItem(6, "Сегодня необходимо принять окончательное решение, независимо от того, связано ли это с профессиональной деятельностью или с какими-то личными делами. Если человек долго в чем-то сомневался или просто не торопил события, то теперь судьба дает ему шанс поставить окончательную точку. Пусть внутренний голос, сердце и ум – подскажут вам правильное решение.", "lovers.jpg"),
                    TarotItem(7, "Сегодня самое время достигать своих целей. И самое главное, сосредоточиться. Если есть какие-то важные и неотложные дела, необходимо их делать именно сегодня. Следует двигаться только вперед, преодолевая все препятствия на своем пути. Только решительные шаги и действия поспособствуют удаче. Но если на данном этапе нет цели, то можно заняться чем-нибудь новым.", "chariot.jpg"),
                    TarotItem(8, "Как карта дня, сила символизирует увлеченность и успех начинаний. Она рекомендует приложить свои таланты к работе или активизироваться в личном плане. В день, на который выпала такая карта, сил хватит на все. Если же возникнут какие-то препятствия, не нужно пугаться. Они будут преодолимы, но действовать надо без агрессии, но решительно, не бояться.", "strength.jpg"),
                    TarotItem(9, "Если Отшельник выпал в качестве карты дня, то в этот день следует полагаться, лишь на себя самого. Решения нужно принимать не спеша, взвешенно и именно что самостоятельно. Если имеются важные дела, делать их надо скрупулезно и ответственно, и опять-таки полагаясь исключительно на собственные возможности.", "hermit.jpg"),
                    TarotItem(10, "Сегодня человека ожидают положительные события в жизни. И что бы ни происходило в судьбе человека, все нужно принимать с благодарностью. Сегодня все не случайно, обращайте свое внимание на знаки судьбы, они что-нибудь подскажут. Карта человека ведет к определенным событиям, которые должны произойти в его жизни.", "wheelfortune.jpg"),

                    TarotItem(11, "Аркан говорит о том, что необходимо очень хорошо и трезво размышлять над текущими делами. Если разрешается какой-либо конфликт, принимаются какие-либо решения, внимательно и дотошно просчитайте все последствия ваших действий. Просмотрите условия договора,не упускайте мелочей.", "justice.jpg"),
                    TarotItem(12, "Сегодняшний день преподаст вам урок терпения. Либо что-то, что и так тянется слишком долго, затянется еще больше, либо же наступит задержка в каком-либо деле, от которого вы этого никак не ожидали. Не пытайтесь ускорить ход событий, особенно силой, — будет только хуже. Возможно, будет достаточно пересмотреть свое отношение к происходящему, взглянуть на все в новом свете.", "hangedman.jpg"),
                    TarotItem(13, "В этот день для человека происходящее станет пройденным этапом (прошлое). Для кого-то это будет приятной вестью, что наконец-то это произошло и все завершилось, а кому-то не захочется расставаться, ведь это время так много значило для него. Но ничего здесь не поделаешь, нужно смириться с обстоятельствами, и подготовиться к тому, чтобы расстаться с прошлым.", "death.jpg"),
                    TarotItem(14, "Этот день можно, по истине, назвать волшебным. У человека произойдут в жизни какие-то новые открытия и события, что-то произойдет, связанное с творчеством. Возможно, у человека получится не только наладить свою жизнь, он сможет в чем-то помочь и другим людям, с удовольствием решит их проблемы. Если есть проблемы в любовных отношениях - наладьте их именно сегодня.", "temperance.jpg"),
                    TarotItem(15, "В этот день человек увидит теневую сторону своего характера. Возможно, кто-то собьет человека с пути и направит его на неблаговидный поступок, а может он и вовсе изменит своим принципам. Не исключено некое внутреннее побуждение, о котором человек даже не подозревал или просто думал, что уже давно все преодолел. Необходимо найти причину этого импульса.", "devil.jpg"),

                    TarotItem(16, "Аркан гласит - в вашей жизни всё изменилось, вы потеряли то, что было вам очень дорого. Однако прошлое не вернуть, нужно извлечь из случившегося урок. Возможно, в будущем вы поймёте, что это потеря была для вас благоприятной. В любом случае принимайте нестандартные решения и не пытайтесь удержать то, что уже начало рассыпаться.", "tower.jpg"),
                    TarotItem(17, "Вам нужно ценить представившиеся вам возможности, верить в себя, вот что советует карта. Высшие силы благоволят вам, даже если ваша цель кажется нереальной, план сумасшедшим, не нужно останавливаться, ведь вы поставили себе цель.Если у вас нет никаких планов, попробуйте взглянуть на свою сегодняшнюю ситуацию, так сказать, с высоты птичьего полета.", "star.jpg"),
                    TarotItem(18, "День может человеку показаться неблагоприятным. Причиной этому может послужить дурной сон, который приснился. Не стоит придавать особого значения своим снам, и пугать себя призраками и мистикой. Если перед человеком стоит какое-то важное дело (пусть даже неприятное и сложное), не следует его избегать. Ведь после преодоления страха обязательно последует что-то новое и интересное.", "moon.jpg"),
                    TarotItem(19, "День запомнится ярким и счастливым. Либо это будет связано с какой-то победой, а возможно, просто день пройдет без проблем и забот. Следует взяться за какое-нибудь новое занятие, оно станет успешным. Не забывайте и о том, что найдутся люди, которые тоже захотят принять в этом участие. Не отказывайте им в такой возможности.", "sun.jpg"),
                    TarotItem(20, "В день, на который выпала такая карта, можно решить любую проблему, и неважно, возникла она вчера или десять лет назад. Все может получиться даже само собой. Но от человека требуется внимательность, чтобы уловить правильный момент для действия. Если это получится – успех гарантирован. В определении нужной минуты следует полагаться исключительно на себя, собственное внутреннее чутье.", "judgement.jpg"),

                    TarotItem(21, "В этот день, как никогда, человек ощущает силы и гармонию с окружающим миром. Он в полной мере может насладиться этим днем, его спокойными и благоприятными очертаниями.Ему будет казаться, что все идет так, как и должно, либо он просто не придаст значения каким-то помехам. Человек почувствует себя счастливым, и это может быть связано с дружбой, любовью, новым жильем.", "world.jpg"),
                    TarotItem(22, "Если человеку в качестве карты дня выпал Шут, то ему предстоит очень много новых и интересных дел. Придется импровизировать, много шутить, быть инициатором и первопроходцем. В этом нет ничего плохого, ведь иногда так полезно снова почувствовать себя беззаботным ребенком, воспринимающим жизнь как веселую игру.", "fool.jpg"),
                    TarotItem(23, "Туз Кубков в такой ситуации – предзнаменование исполнения заветного желания. Все, что загадывается в течение этого дня, обязательно сбудется, воспользуйтесь таким шансом. Карта указывает на перемены в амурных делах. Также аркан предостерегает от пустых мечтаний, которые не сулят ничего благоприятного.", "1cups.jpg"),
                    TarotItem(24, "День запомнится сердечными делами, где будут присутствовать глубокая симпатия, любовь, душевный контакт или примирение. Если есть любимый человек, который уже супруг (супруга), то будет период, когда можно вновь пережить те первые отношения и влюбленность, которые не увяли до сих пор, а даже укрепились.", "2cups.jpg"),
                    TarotItem(25, "Сегодняшний день обязательно принесет с собой радостное событие или благую весть. Отметьте это, поделитесь своим хорошим настроением с близким окружением. Пригласите родных и друзей к себе на уютный вкусный ужин, распейте прекрасную бутылку вина, оставленную когда-то про запас. Не жалейте, этот момент уже наступил, а такой отличный повод бывает далеко не каждый день.", "3cups.jpg"),

                    TarotItem(26, "Сегодня любая мелочь способна подпортить настроение. Не исключены проблемы со здоровьем. Если появилась причина, которая вызывает у вас такие чувства как грусть и тоска, не увлекайтесь этими эмоциями, продолжайте быть открытыми к миру, который обязательно поможет вам реабилитироваться. Кроме того, вокруг много возможностей, которые не стоит упускать по собственной глупости.", "4cups.jpg"),
                    TarotItem(27, "Случатся неизбежные огорчения. Даже если кому-то казалось, что успех на его стороне, а в делах все получается, то этот день – разочарует. Если это как-то связано с мелкими недочетами, то стоит отнестись к этому с юмором. Но если приходится грустить по поводу серьезного дела, то нужно все принять достойно, ничего не скрывая от других людей.", "5cups.jpg"),
                    TarotItem(28, "День подарит человеку воспоминания.Он сможет разобраться в себе: чего он хотел, о чем мечтал в прошлом, что сбылось и не сбылось из этого до сегодняшнего дня. Благодаря такому вот анализу, могут осуществиться многие забытые мечты. Прошлое может дать подсказку для настоящего и будущего. Может произойти встреча с тем, кого давно не видели, а может быть найдется вещь, которая была потерянной. ", "6cups.jpg"),
                    TarotItem(29, "Семерка Кубков советует сегодня быть в ясном уме, чтобы обращать внимание на все мелочи. Не поддавайтесь обману, особенно не лгите самому себе. Не принимайте никаких предложений в этот день, даже если кажется, что они принесут успех, это не так. Судьба проверяет вас. Не употребляйте алкоголь, который нарушит трезвость ума. В таком состоянии вы особенно уязвимы.", "7cups.jpg"),
                    TarotItem(30, "Откажитесь от привычного пути, идите непротоптанной тропинкой, она обязательно выведет вас к чему-то новому. Но не стоит вступать в путь, если на сердце тяжело, нет абсолютно никакого желания это делать. В раскладе карта выпадает как знак расставания с чем-то хорошим. Не стоит огорчаться. Отпустите это от себя, начните искать. Поверьте, другие события и люди смогут обрадовать вас не меньше.", "8cups.jpg"),

                    TarotItem(31, "Заканчивайте напряженную работу, наступила пора праздника и развлечений. Однако не увлекайтесь банкетами: это приводит к обжорству и злоупотреблению спиртными напитками, что гарантирует проблемы с пищеварительной системой. Если серьезных причин для торжества нет, побалуйте себя лишним выходным, полентяйничайте, займитесь тем, что давно хотели делать. Проведите время с семьей, друзьями. ", "9cups.jpg"),
                    TarotItem(32, "Сегодня обязательно появится повод для радости. Вы успешно завершите дело на работе, вследствие чего ждет повышение. Возлюбленный наконец-то сделает предложение руки и сердца, которого вы так долго ждали. Если гадающий одинок, велика вероятность встречи с кем-то, кто в будущем сыграет не последнюю роль, возможно создание серьезных отношений.", "10cups.jpg"),
                    TarotItem(33, "Судьба на вашей стороне. Перед человеком открываются новые возможности и эмоциональная подпитка. Сегодня тот самый день, когда человек может принимать подарки судьбы, при этом ничего не отдавая взамен. Это может даже проявляться в обычных мелочах (комплименты, объяснения в любви, почетный компромисс в непростых обстоятельствах). Его начинания дадут плоды", "pagecups.jpg"),
                    TarotItem(34, "День будет довольно светлым и наполненным событиями. Настроение и самочувствие прекрасное, а энергия бьет ключом. Используйте день по назначению, решите дела, на которые давно не хватало времени и сил. Этот день будет благополучным для разрешения конфликтов в любовных отношениях. Фантазия будет работать безупречно, можно поговорить по душам, обсудив все наболевшее. ", "knightcups.jpg"),
                    TarotItem(35, "В этот день гадающий будет особо чутко чувствовать эмоциональное настроение окружающих людей. Есть вероятность, что из-за чужого поведения он может пострадать. Будьте внимательны, судьба посылает повсюду знаки. Оставайтесь верными самому себе, своим принципам, чувствам. Однако не принимайте то, чем страстно желаете обладать, за чистую монету, это происходит не в реальности.", "queencups.jpg"),

                    TarotItem(36, "Карта выпадает в том случае, когда нужно немного сдерживать свои чувства, принимая решения, в первую очередь, согласовывая их с разумом, а не сердцем. Однако не скупитесь на эмоции с любимым человеком или с тем, кто жаждет вашей заботы и внимания. Поделитесь тем, что вас беспокоит, за что болит душа. В ответ получите только приятные слова поддержки, мудрые и действенные советы.", "kingcups.jpg"),
                    TarotItem(37, "Сегодня удача будет преследовать по пятам. Будьте предельно внимательны, не упустите свои шансы, они предостерегают вас в разных местах и в самое неожиданное время. Вложитесь в те планы, которые являются долгосрочными и реальными, особенно если это касается карьеры и заработка. Если все планы только краткосрочные, то сегодняшний день станет отличным стартом для далеко идущих задач.", "1pentacles.jpg"),
                    TarotItem(38, "Сегодня не нужно слишком серьезно относиться к своей трудовой деятельности. Вполне будет достаточно немного игривости, гибкости и легкомысленности. Не стоит отказываться от возможности – попробовать что-то новое, чтобы проверить свои способности. И даже если что-то не получится сразу, всегда можно повторить попытку. В этот день человек будет хорошо приспосабливаться к полученным заданиям.", "2pentacles.jpg"),
                    TarotItem(39, "В качестве карты дня, тройка пентаклей рекомендует отказаться от тормозящих развитие пережитков прошлого и перейти на качественно новый уровень. Форма этого отказа может быть различной – можно выяснить, наконец, отношения с партнером, поменять работу или закончить давно начатую работу. В любом случае, это должно быть окончание рутинного этапа прошлой жизни, позволит начать новое дело.", "3pentacles.jpg"),
                    TarotItem(40, "Стоит задуматься, правильно ли выбрано направление приложения усилий. Нередко бывает, что карта указывает на то, что человек тратит все свои силы на действия, которые лишь вредят реализации его планов. Карта советует тщательнее спланировать день, чтобы все успеть, быть скромнее в тратах.", "4pentacles.jpg"),

                    TarotItem(41, "Не стоит строить на такой день далеко идущие планы – все равно дела пойдут не так, как надо. Возможно, стоит подумать о том, чтобы поменять жизненные приоритеты. Возможно, впереди ожидается неблагоприятная полоса в жизни. Но жизнь, как известно, похожа на зебру, поэтому следует принять предстоящие испытания с достоинством, терпеливо перенести их, и дело быстро наладится.", "5pentacles.jpg"),
                    TarotItem(42, "Сегодня будет день помощи другим людям. Не скупитесь на благое дело, будьте щедры и позже это вернется вам в большем размере, когда трудности в жизни возникнут и у вас. Если нет возможности дать взаймы, помогите физически, как минимум, выслушайте человека со всем вниманием, дайте ценный и мудрый совет.", "6pentacles.jpg"),
                    TarotItem(43, "Сегодняшний день явно не для спешки, стоит быть более терпеливыми, неспешными. Принимайте во внимание каждую деталь, взвешивайте в голове каждое решение, прежде чем утвердить его. Любая оплошность сегодня спровоцирует появление сложных ситуаций, решение которых потребует в будущем больших материальных и энергетических затрат. Нет смысла нарушать планы, которые мучительно претворяются в жизнь.", "7pentacles.jpg"),
                    TarotItem(44, "Сегодня предстоит день новых возможностей и желаний. Стоит попробовать взяться за новое дело, пойти учиться или развивать навыки мастерства. Успех преследует вас по пятам, поэтому, за что бы вы, сегодня не взялись, все разрешится благополучно. Главным условием является не переоценивать собственные силы. Не берите на себя много забот, если понимаете, что не справитесь за отведенное время.", "8pentacles.jpg"),
                    TarotItem(45, "Карта говорит о том, что гадающего ждут приятные известия, которые оставят положительный отпечаток в судьбе. Нужно принимать дары судьбы и быть благодарным за это. Этот день полон приятных событий, поэтому нужно быть решительней в новых делах, и тогда фортуна обязательно будет на вашей стороне.", "9pentacles.jpg"),

                    TarotItem(46, "Сегодняшний день просто создан для подведения итогов. Приготовьтесь отпустить от себя что-то важное, чтобы получить нечто ценное. Все неприятности и горести могут закончиться уже сейчас. День пройдет спокойно и радостно, оставив за собой приятные воспоминания. И, возможно, станет одной из значимых дат в календаре.", "10pentacles.jpg"),
                    TarotItem(47, "Сегодня будет шанс получить то, о чем даже и не мечтали. Не стоит отказываться от выгодных сделок, помогайте людям, оказывайте им практическую помощь и материальную поддержку. В личной жизни ожидаются перемены, которые будут связаны исключительно с приятными событиями и ощущениями. Человек получит полезный совет от партнера, который подвигнет его на создание прочного и долговременного союза.", "pagepentacles.jpg"),
                    TarotItem(48, "Не позволяйте обвести себя вокруг пальца. Человек под воздействием аркана будет способен говорить на серьезные темы, в дело он пустит лишь проверенные способы, и не пойдет на спекуляции. Именно в этот день есть отличный шанс принять важное и ответственное решение, даже если проблема казалась трудной и невыполнимой. Но, а если нет никаких проблем , то самое время для того, чтобы расслабиться.", "knightpentacles.jpg"),
                    TarotItem(49, "Нельзя позволять другим людям манипулировать собой. Сейчас все зависит от вас, просто последовательно выполняйте свою работу и помните о долгосрочных проектах. Не исключена встреча с женщиной, которая обладает сильными материнскими чувствами и добротой. Именно эта женщина может сыграть важную роль в судьбе гадающего человека. И стоит прислушаться к тому, что она будет говорить.", "queenpentacles.jpg"),
                    TarotItem(50, "Сегодняшний день словно создан для того, чтобы подводить итоги своей работы. Если результаты хорошие, порадуйтесь за себя. Может возникнуть потребность взяться за новое дело, не отказывайте себе в этом желании. Сохраняйте нажитое, ведь это было достигнуто непосильным трудом в течение длительного времени, а потратить все можно за несколько минут.", "kingpentacles.jpg"),

                    TarotItem(51, "Решение задачи, над которой долгое время приходилось ломать голову, будет прямо перед носом. Будьте сегодня особенно внимательны, чтобы не упустить эту возможность, каждый день такие шансы на дороге не валяются. Вероятно поступление новой полезной информации, будьте внимательны к новостям и неожиданным денежным поступлениям.", "1swords.jpg"),
                    TarotItem(52, "Сегодня лучше не принимать важных решений и не начинать ничего нового. День хорош для отдыха, медитаций, планирования и работы над своим внутренним состоянием. Он требует терпения, спокойствия и выдержки. А еще сегодня лучше не слушать свою интуицию, она может вас обмануть. Старайтесь не вмешивайтесь в ссоры и избегать конфликтов.", "2swords.jpg"),
                    TarotItem(53, "Если гадающему выпала Тройка Мечей, то нужно приготовиться к тому, что день будет тяжелым. Придется принимать сложные и неприятные решения, смотреть в лицо неудачам и потерям, отказываться от чего-то важного, прощаться с близкими людьми с тоской на сердце. Это не самый лучший день для начинаний, новых предложений и знакомств. Ведь есть большой риск разочароваться, потерять веру в себя.", "3swords.jpg"),
                    TarotItem(54, "егодня нужно решить волнующий вопрос, который давно откладывался до лучших времен. Лучше не станет, тянуть дальше некуда, поэтому предпочтительно урегулировать все сейчас, пока не наступила точка невозврата. Можно обратиться за советом к близким и на основе их видения ситуации сделать собственные выводы. Каким бы неприятным не оказалось решение — на душе станет легче, перемены пойдут на пользу. ", "4swords.jpg"),
                    TarotItem(55, "В прямом положении Пятерка Мечей как карта дня рекомендует не торопиться в принятии решений. Впереди много событий, как негативных, так и позитивных, поэтому неудивительно, если к концу вечера будет чувствоваться упадок сил. Не рубите с плеча, а внимательно и осторожно прощупайте почву. Карта заставляет пересмотреть планы, остаться дома, изменить привычный маршрут.", "5swords.jpg"),

                    TarotItem(56, "Сегодня вас ожидают перемены, или вам предстоит нечто необычное, и вы переживаете по этому поводу. Главное, — не опускать руки и не отказываться. Сделайте требуемый шаг решительно и трезво, а потом осмотритесь. Наверняка найдется кто-то, кто сможет помочь вам. Однако, будьте внимательны и уходите оттуда, где вас провоцируют на конфликт.", "6swords.jpg"),
                    TarotItem(57, "Сегодня надо быть особенно осторожным, так как не исключен обман. Если предстоит подписывать какие-то документы, особенно внимательно стоит прочесть все пункты, чтобы потом не страдать от собственной неграмотности. День будет тревожным и не слишком удачным. Проверяйте подлинность банкнот, сказанные слова и предъявленные доказательства. В такой день лучше остаться дома и переждать полосу невезения.", "7swords.jpg"),
                    TarotItem(58, "Сегодня судьба столкнет человека с запретом. Он будет чувствовать себя зажатым и скованным, могут произойти и совершенно другие ограничения. Необходимо взять себя в руки, сделать шаг, и попробовать научиться преодолеть этот этап. Если покажется, что это скованное состояние так никуда и не ушло, то придется найти другой путь.", "8swords.jpg"),
                    TarotItem(59, "Сегодня карта характеризует события, как кошмарный сон. Но так они представлены перед человеком, которого мучают угрызения совести. На самом деле, ничего отрицательного этот день не несет. Даже если у человека будут трудности, то это все временные обстоятельства. Нужно просто собраться с духом и принять ситуацию как есть. Если человек чувствует себя виноватым, то ему нужно раскаяться в содеянном.", "9swords.jpg"),
                    TarotItem(60, "Сегодня можно подвести итоги. И даже если речь идет о чем-то или ком-то близком и дорогом, нужно быть готовым проститься. Произойдет завершение неприятностей и огорчений, которым человек будет искренне радоваться. И главное, ничего не разрушить, а то придется об этом потом долго сожалеть. Откройте себя новым делам и свершениям, переживите страшную потерю так, чтобы не сломаться духом.", "10swords.jpg"),

                    TarotItem(61, "Карта дня говорит о том, что человек услышит критику в свой адрес, его предложения, скорее всего, будут отвергнуты. Нужно просто подготовить себя к этому изначально, чтобы потом не расстраиваться и не вставать в контратаку. Между прочим, критика может оказаться очень полезной, так как несет в себе что-то ценное. Если человек прислушается к сказанным словам, то в его жизни многое прояснится.", "pageswords.jpg"),
                    TarotItem(62, "Карта дня свидетельствует о конфликте, именно поэтому необходимо ясно мыслить и трезво реагировать на происходящее, и даже в какой-то степени хладнокровно. Дискутировать, желательно, без эмоций. Благодаря этому даже в сложной ситуации можно надеяться на победу. Но если окажется «ничья», это также пойдет на пользу, а не во вред.", "knightswords.jpg"),
                    TarotItem(63, "Судьба человеку подскажет сделать правильный шаг. Если совсем недавно он еще от кого-то был зависим, то теперь настал тот день, когда все значительно меняется в лучшую сторону. Теперь ничего не нужно стесняться, нужно вести себя свободно и непринужденно при любых обстоятельствах.", "queenswords.jpg"),
                    TarotItem(64, "Судьба преподносит гадающему человеку экзамен, где ему необходимо показать свои знания, сообразительность и способность пользоваться собственным опытом. Гадающий, должен хорошо подготовиться к этому испытанию, и для этого у него должны быть убедительные проекты и аргументы, ну и было бы не лишним воспользоваться своим обаянием и остроумием.", "kingswords.jpg"),
                    TarotItem(65, "На сегодняшний день у человека, которому гадают, хватит энергии для того, чтобы заняться новым проектом. Можно сдвинуть «с мёртвой точки» нерешённые вопросы и проблемы. Какая бы идея человека не посетила, он может смело браться за её реализацию. У него всё получится. Увлечённость творческими находками не оставит равнодушными его коллег. Возможно, что появится информация, которая откроет новые возможности.", "1wands.jpg"),

                    TarotItem(66, "День указывает на то, что человек проявляет свою нерешительность. В момент, когда необходимо принять решение или взяться за какую-то работу, он делает ход назад. Просто под влиянием Аркана он не может сосредоточиться и у него ничего не получается. Отбросим все сомнения, самое верное решение – занять нейтральную сторону. Если кто-то будет от вас требовать больше, чем вы можете сделать, так и скажите.", "2wands.jpg"),
                    TarotItem(67, "Человек, на которого делается расклад, имеет чёткие жизненные ориентиры. Он на правильном пути к своему духовному развитию. Сегодня, возможно, кто-то из коллег предложит подработку. Возможно - совместное участие в деле, и поэтому нужно соглашаться. Совместная деятельность позволит реализовать творческий потенциал. Использовав этот шанс, гадающий человек может подумать о смене профессии.", "3wands.jpg"),
                    TarotItem(68, "Сегодня - тот самый день, когда можно делать всё, что просит душа. Можно смело знакомиться с новыми людьми, ведь есть шанс, что среди них могут оказаться те, кто станут верными друзьями. Любимой (или любимому) можно сделать подарок или организовать настоящий праздник. Если есть человек, с которым гадающий в ссоре - это лучший день для примирения. Грустные мысли надо гнать от себя прочь в этот день.", "4wands.jpg"),
                    TarotItem(69, "Сегодня вам предстоит испытание, поэтому нужно быть максимально к нему готовым. Не стоит переживать по этому поводу, ведь это своего рода экзамен, по итогу которого, вы сможете подтвердить себе вашу способность решать задачи, что позитивно скажется на вашей самооценке, к примеру, противоборство с близким человеком или конкуренции на работе, и там нельзя давать слабину, нужно показать что вы в отличной форме.", "5wands.jpg"),
                    TarotItem(70, "День будет очень удачный, принесет множество приятных сюрпризов в виде разрешившихся проблем, над которыми гадающий долгий период времени хлопотал. Начатые недавно дела пойдут в гору. Стоит помнить о том, что успешное разрешение будет только у тех дел, которые приносили головную боль и большие растраты, то есть достижение цели будет полностью оправданным и заслуженным.", "6wands.jpg"),

                    TarotItem(71, "День, когда может произойти очень важное дело в жизни, и не упустите этот момент. Могут появиться соперники и недоброжелатели, которые попытаются навредить вам, не дайте им этого сделать, бросайтесь в бой. У человека под влиянием карты Семерка жезлов будут неоспоримые права для того, чтобы напомнить своим врагам о том, что с его мнением нужно считаться. Только благодаря решительности, он достигнет желаемого успеха.", "7wands.jpg"),
                    TarotItem(72, "День, когда человек может получить от судьбы радость и благополучие. Будут происходить только положительные события и желанные перемены. Стоит ожидать хороших известий (в виде письма, телефонного звонка). Человек запомнит этот день как удачный и счастливый. Карта советует вам активнее действовать, чтобы достичь желаемого.", "8wands.jpg"),
                    TarotItem(73, "Девятка жезлов предупреждает гадающего: сегодня никому ничего не стоит говорить о себе и своих планах. Это необходимо, чтобы никто не мог на человека оказать негативного влияния. Лучше побыть в одиночестве. Особенно необходимо остерегаться вступать в диалог с теми людьми, с кем недавно была ссора. Возможно повторение конфликта.", "9wands.jpg"),
                    TarotItem(74, "Этот день для гадающего окажется сложным. Чтобы достичь поставленных целей, придётся приложить максимум усилий. С первого раза может ничего не получится. Какие бы проблемы не возникли сегодня, гадающий сможет их благополучно решить. Энергии и мудрости для этого ему хватит. Возможно, придётся потрудиться над новым проектом. Сразу может показаться, что он трудный.", "10wands.jpg"),
                    TarotItem(75, "В качестве карты дня Паж Жезлов предвещает яркое, необычное событие. Но нужно быть внимательным и не пропустить признака предстоящей приятной неожиданности. Это может быть и повышение по службе, и приятная встреча, и «роман века». Но это и вовсе не означает, что событие будет судьбоносным. Возможно, это окажется просто очень интересный и приятный эпизод, что превратится в сюжет для рассказов внукам.", "pagewands.jpg"),

                    TarotItem(76, "Непросто будет гадающему в этот день. Возможно, предстоит показать себя окружающим не с лучшей стороны. Это может быть и ссора, и неприятности. В этот день не стоит начинать новые дела и реализовывать задуманное. Для этого подойдут другие дни.", "knightwands.jpg"),
                    TarotItem(77, "День для решительных действий, когда человек будет ощущать в себе уверенность и сильную энергетику. Любая, даже самая трудная работаему будет по плечу. Он готов делать решающие шаги, проявляет в себе храбрые качества и оптимистический настрой к делу. А окружающие люди это заметят, они пойдут за вами, потому что многим в жизни так не хватает человеческой поддержки, оптимизма и мудрых советов.", "queenwands.jpg"),
                    TarotItem(78, "Хорошее предзнаменование – вот о чём говорит данная карта. Дела складываются успешно. Возможно, что какой –то человек окажет помощь в одной из сфер жизни. Надо готовиться к получению неожиданной прибыли, предложении о новой должности. Руководство по достоинству сможет оценить энтузиазм человека. Его трудолюбие будет вознаграждено по достоинству.", "kingwands.jpg")
                )
            }
        }
    }
}